mapper.insert:
insert into guarantee (rname, rcontent, rpublicdate,
  raccpetdate, rcompletiondate, maintenanceuserid,
  rstate, revaluation, `uid`,
  aid, raddress, rimg
  )
values (#{rname,jdbcType=VARCHAR}, #{rcontent,jdbcType=VARCHAR}, #{rpublicdate,jdbcType=TIMESTAMP},
  #{raccpetdate,jdbcType=TIMESTAMP}, #{rcompletiondate,jdbcType=TIMESTAMP}, #{maintenanceuserid,jdbcType=INTEGER},
  #{rstate,jdbcType=INTEGER}, #{revaluation,jdbcType=VARCHAR}, #{uid,jdbcType=VARCHAR},
  #{aid,jdbcType=VARCHAR}, #{raddress,jdbcType=VARCHAR}, #{rimg,jdbcType=VARCHAR}
  )

mapper.select:
select 
from guarantee


    and uid = #{uid}


        and rstate  = #{rstate}


limit #{start},#{limit}

select 
from guarantee

 `uid` in 
#{v.uid}


   and rstate = #{rstate}


limit #{start},#{limit}

